import { useState, useEffect } from "react";

const ProductAdd = () => {

    const [price, setPrice] = useState('');
    const [title, setTitle] = useState('');

    const submitProduct = async (e) => {
        e.preventDefault();
        await axios.post('http://localhost:3000/product/add')
    }

    return (
        <div className="card container mt-5">
            <div className="container m-5 p-5">
            <form>
                <div className="field">
                    <label className="label">Title</label>
                    <input class="input" type="text" placeholder="Title" value={title} onChange={ (e) => setTitle(e.target.value) }></input>
                </div>
                <div className="field">
                    <label className="label">Price</label>
                    <input class="input" type="text" placeholder="Price" value={price} onChange={ (e) => setPrice(e.target.value) }></input>
                </div>

                <div className="field">
                    <button className="button is-primary">Submit</button>
                </div>
            </form>
            </div>
        </div>
    )
}

export default ProductAdd;
