  type = OperationType.OVERWRITE;

  constructor(
    public source: OperationSource,
    public path: Path,
    public snap: Node
  ) {}

  operationForChild(childName: string): Overwrite {
    if (pathIsEmpty(this.path)) {
      return new Overwrite(
        this.source,
        newEmptyPath(),
        this.snap.getImmediateChild(childName)
      );
