import { stringify } from '@firebase/util';

import { DataSnapshot as ExpDataSnapshot } from '../../exp/Reference_impl';
import { Path } from '../util/Path';

import { EventRegistration } from './EventRegistration';

/**
 * Encapsulates the data needed to raise an event
 * @interface
 */
export interface Event {
  getPath(): Path;

  getEventType(): string;

  getEventRunner(): () => void;

  toString(): string;
}

/**
 * One of the following strings: "value", "child_added", "child_changed",
 * "child_removed", or "child_moved."
 */
export type EventType =
  | 'value'
  | 'child_added'
  | 'child_changed'
  | 'child_moved'
  | 'child_removed';

/**
 * Encapsulates the data needed to raise an event
 */
export class DataEvent implements Event {
  /**
   * @param eventType - One of: value, child_added, child_changed, child_moved, child_removed
   * @param eventRegistration - The function to call to with the event data. User provided
   * @param snapshot - The data backing the event
   * @param prevName - Optional, the name of the previous child for child_* events.
   */
  constructor(
    public eventType: EventType,
    public eventRegistration: EventRegistration,
    public snapshot: ExpDataSnapshot,
    public prevName?: string | null
  ) {}
  getPath(): Path {
    const ref = this.snapshot.ref;
    if (this.eventType === 'value') {
      return ref._path;
    } else {
      return ref.parent._path;
    }
  }
  getEventType(): string {
    return this.eventType;
  }
  getEventRunner(): () => void {
    return this.eventRegistration.getEventRunner(this);
  }
  toString(): string {
    return (
      this.getPath().toString() +
      ':' +
      this.eventType +
      ':' +
      stringify(this.snapshot.exportVal())
    );
  }
}

export class CancelEvent implements Event {
  constructor(
    public eventRegistration: EventRegistration,
    public error: Error,
    public path: Path
  ) {}
  getPath(): Path {
    return this.path;
  }
  getEventType(): string {
    return 'cancel';
  }
  getEventRunner(): () => void {
    return this.eventRegistration.getEventRunner(this);
  }
  toString(): string {
    return this.path.toString() + ':cancel';
  }
}
