import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";
import jwt_decode from "jwt-decode";

const UserEdit = () => {

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [token, setToken] = useState([]);
    const [expire, setExpire] = useState([]);
    const navigate = useNavigate();
    const { id } = useParams();

    useEffect(() => {
        refreshToken();
        getUserByID();
    }, []);

    const refreshToken = async () => {
        try {
            const response = await axios.get('http://localhost:3001/token');
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setExpire(decoded.exp);
        } catch (error) {
            if (error.response) {
                navigate('/')
            }
        }
    }

    const axiosJWT = axios.create()

    axiosJWT.interceptors.request.use(async (config) => {
        const currentDate = new Date();
        if (expire * 1000 < currentDate.getTime()) {
            const response = await axios.get('http://localhost:3001/token');
            config.headers.Authorization = `Bearer ${response.data.accessToken}`;
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setExpire(decoded.exp);
        }
        return config;
    }, (error) => {
        return Promise.reject(error);
    });

    const getUserByID = async () => {
        const response = await axiosJWT.get('http://localhost:3001/user/' + id, {
            headers: {
                Authorization: `Bearer ${token}`
            }
        });
        setName(response.data.name);
        setEmail(response.data.email);
    }

    const submitUser = async (e) => {
        await axios.patch('http://localhost:3001/user/edit/' + id, {
            headers: {
                Authorization: `Bearer ${token}`
            },
            body: {
                name: name,
                email: email,
                password: password
            }
        });
    }

    return (
        <div className="card container mt-5">
            <div className="m-5 p-5">
            <form onSubmit={submitUser}>
                <div className="field">
                    <label className="label">Name</label>
                    <input className="input" type="text" placeholder="Name" value={name} onChange={ (e) => setName(e.target.value) }></input>
                </div>
                <div className="field">
                    <label className="label">Email</label>
                    <input className="input" type="email" placeholder="Email" value={email} onChange={ (e) => setEmail(e.target.value) }></input>
                </div>
                <div className="field">
                    <label className="label">Password</label>
                    <input className="input" type="text" placeholder="Password" onChange={ (e) => setPassword(e.target.value) }></input>
                </div>

                <div className="field">
                    <button type="submit" className="button is-primary">Submit</button>
                </div>
            </form>
            </div>
        </div>
    )
}

export default UserEdit;
