import React, { useState, useEffect } from 'react';
import axios from 'axios';
import jwt_decode from "jwt-decode";
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
    const [name, setName] = useState('');
    const [token, setToken] = useState('');
    const [expire, setExpire] = useState('');
    const [users, setUsers] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        refreshToken();
        getUsers();
    }, []);

    const refreshToken = async () => {
        try {
            const response = await axios.get('http://localhost:3001/token');
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setName(decoded.name);
            setExpire(decoded.expire);
        } catch (error) {
            if (error.response) {
                navigate('/')
            }
        }
    }

    const axiosJWT = axios.create()

    axiosJWT.interceptors.request.use(async (config) => {
        const currentData = new Date();
        if (expire * 1000 < currentData.getTime()) {
            const response = await axios.get('http://localhost:3001/token');
            config.headers.Authorization = `Bearer ${response.data.accessToken}`;
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setName(decoded.name)
            setExpire(decoded.expire)
        }
        return config;
    }, (error) => {
        return Promise.reject(error);
    });

    const getUsers = async () => {
        const response = await axiosJWT.get('http://localhost:3001/users', {
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
        setUsers(response.data);
    }

    const createProduct = async () => {
        const response = await axiosJWT.get('http://localhost:3001/users')
        setUsers(response.data);
    }

  return (
    <div className='container mt-5'>
        <h1 className="title">Welcome back: {name}</h1>
        <button className='button is-info' onClick={getUsers}>Get Users</button>
        <table className='table is-striped is-fullwidth'>
            <thead>
                <tr>
                    <th>NO</th>
                    <th>Name</th>
                    <th>Email</th>
                </tr>
            </thead>
            <tbody>
            {users.map((user, index) => (
                <tr key={user.id}>
                    <th>{index + 1}</th>
                    <th>{user.name}</th>
                    <th>{user.email}</th>
                </tr>
            ))}
            </tbody>
        </table>

        <button className='button is-info' onClick={getUsers}>Creates</button>
        <table className='table is-striped is-fullwidth'>
            <thead>
                <tr>
                    <th>NO</th>
                    <th>Name</th>
                    <th>Email</th>
                </tr>
            </thead>
            <tbody>
            {users.map((user, index) => (
                <tr key={user.id}>
                    <th>{index + 1}</th>
                    <th>{user.name}</th>
                    <th>{user.email}</th>
                </tr>
            ))}
            </tbody>
        </table>
    </div>
  )
}

export default Dashboard
