import axios from "axios";
import { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import jwt_decode from "jwt-decode";

const UserList = () => {

    const [users, setUsers] = useState([]);
    const [token, setToken] = useState([]);
    const [expire, setExpire] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        getUsers();
        refreshToken();
    });

    const refreshToken = async () => {
        try {
            const response = await axios.get('http://localhost:3001/token');
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setExpire(decoded.exp);
        } catch (error) {
            if (error.response) {
                navigate('/')
            }
        }
    }

    const axiosJWT = axios.create()

    axiosJWT.interceptors.request.use(async (config) => {
        const currentData = new Date();
        if (expire * 1000 < currentData.getTime()) {
            const response = await axios.get('http://localhost:3001/token');
            config.headers.Authorization = `Bearer ${response.data.accessToken}`;
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setExpire(decoded.exp);
        }
        return config;
    }, (error) => {
        return Promise.reject(error);
    });

    const getUsers = async () => {
        const response = await axiosJWT.get('http://localhost:3001/user', {
            headers: {
                Authorization: `Bearer ${token}`
            }
        });
        setUsers(response.data);
    }

    const deleteUser = async (id) => {
        await axios.delete('http://localhost:3001/user/delete/' + id);
        getUsers();
    }

    return (
        <div className='container mt-5'>
            
            <Link to="/user/add" className="button is-primary">Add User</Link>
            <table className="table is-striped is-fullwidth">
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    { users.map((user, index) => (
                    <tr key={ user.id }>
                        <td>{ index + 1 }</td>
                        <td>{ user.name }</td>
                        <td>{ user.email }</td>
                        <td className="is-actions-cell">
                            <div className="buttons">
                                <Link to={'/user/edit/' + user.id} className="button is-small is-primary" type="button">
                                    <span className="icon"><i className="mdi mdi-eye"></i></span>
                                </Link>
                                <button onClick={() => deleteUser(user.id)} className="button is-small is-danger jb-modal" data-target="sample-modal" type="button">
                                    <span className="icon"><i className="mdi mdi-trash-can"></i></span>
                                </button>
                            </div>
                        </td>
                    </tr>
                    )) }
                </tbody>
            </table>
        </div>
    )
}

export default UserList;
