import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";
import jwt_decode from "jwt-decode";

const ProductEdit = () => {

    const [price, setPrice] = useState('');
    const [title, setTitle] = useState('');
    const [token, setToken] = useState([]);
    const [expire, setExpire] = useState([]);
    const navigate = useNavigate();
    const { id } = useParams();

    const submitProduct = async (e) => {
        e.preventDefault();
        await axios.patch(`http://localhost:3001/product/edit/${id}`, {
            title: title,
            price: price
        });
        navigate('/product');
    }

    useEffect(() => {
        refreshToken()
        getProductByID();
    }, []);

    const refreshToken = async () => {
        try {
            const response = await axios.get('http://localhost:3001/token');
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setExpire(decoded.exp);
        } catch (error) {
            if (error.response) {
                navigate('/')
            }
        }
    }

    const getProductByID = async () => {
        const response = await axios.get(`http://localhost:3001/product/edit/${id}`);
        setTitle(response.data.title);
        setPrice(response.data.price);
    }

    return (
        <div className="card container mt-5">
            <div className="m-5 p-5">
            <form onSubmit={submitProduct}>
                <div className="field">
                    <label className="label">Title</label>
                    <input className="input" type="text" placeholder="Title" value={title} onChange={ (e) => setTitle(e.target.value) }></input>
                </div>
                <div className="field">
                    <label className="label">Price</label>
                    <input className="input" type="text" placeholder="Price" value={price} onChange={ (e) => setPrice(e.target.value) }></input>
                </div>

                <div className="field">
                    <button type="submit" className="button is-primary">Submit</button>
                </div>
            </form>
            </div>
        </div>
    )
}

export default ProductEdit;
