import axios from "axios";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const ProductsList = () => {

    const [products, setProduct] = useState([]);

    useEffect(() => {
        getProducts();
    });

    const getProducts = async () => {
        const response = await axios.get('http://localhost:3001/product');
        setProduct(response.data);
    }

    const deleteProduct = async (id) => {
        const response = await axios.delete('http://localhost:3001/product/delete/' + id);
        getProducts();
    }

    return (
        <div className='container mt-5'>
            
            <Link to="/product/add" className="button is-primary">Add Product</Link>
            <table className="table is-striped is-fullwidth">
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Title</th>
                        <th>Price</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    { products.map((product, index) => (
                    <tr key={ product.id }>
                        <td>{ index + 1 }</td>
                        <td>{ product.title }</td>
                        <td>{ product.price }</td>
                        <td className="is-actions-cell">
                            <div className="buttons">
                                <Link to={'/product/edit/' + product.id} className="button is-small is-primary" type="button">
                                    <span className="icon"><i className="mdi mdi-eye"></i></span>
                                </Link>
                                <button onClick={() => deleteProduct(product.id)} className="button is-small is-danger jb-modal" data-target="sample-modal" type="button">
                                    <span className="icon"><i className="mdi mdi-trash-can"></i></span>
                                </button>
                            </div>
                        </td>
                    </tr>
                    )) }
                </tbody>
            </table>
        </div>
    )
}

export default ProductsList;
