import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import jwt_decode from "jwt-decode";

const UserAdd = () => {

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [token, setToken] = useState('');
    const [expire, setExpire] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        refreshToken();
    });

    const refreshToken = async () => {
        try {
            const response = await axios.get('http://localhost:3001/token');
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setExpire(decoded.exp);
        } catch (error) {
            if (error.response) {
                navigate('/')
            }
        }
    }

    const axiosJWT = axios.create()

    axiosJWT.interceptors.request.use(async (config) => {
        const currentData = new Date();
        if (expire * 1000 < currentData.getTime()) {
            const response = await axios.get('http://localhost:3001/token');
            config.headers.Authorization = `Bearer ${response.data.accessToken}`;
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setExpire(decoded.exp);
        }
        return config;
    }, (error) => {
        return Promise.reject(error);
    });

    const submitUser = async (e) => {
        e.preventDefault();
        await axiosJWT.post('http://localhost:3001/user/add', {
            headers: {
                Authorization: `Bearer ${token}`
            },
            name: name,
            email: email,
            password: password
        });
        navigate('/user');
    }

    const backClick = () => {
        navigate(-1);
    }

    return (
        <><div id="app">
            <Navbar />
            <Sidebar />
            <section className="section is-title-bar">
                <div className="level">
                    <div className="level-left">
                        <div className="level-item">
                            <ul>
                                <li>Admin</li>
                                <li>Product</li>
                            </ul>
                        </div>
                    </div>
                    <div className="level-right">
                        <div className="level-item">
                            <div className="buttons is-right">
                                <button onClick={backClick} className="button is-danger">
                                    <span className="icon"><i className="mdi mdi-arrow-left"></i></span>
                                    <span>Kembali</span>
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="card">
                    <header className="card-header">
                        <p className="card-header-title">
                            <span className="icon"><i className="mdi mdi-book-information-variant default"></i></span>
                            Form Tambah Product
                        </p>
                    </header>
                    <div className="card-content">
                        <form onSubmit={submitProduct}>
                            <div className="field is-horizontal">
                                <div className="field-label is-normal">
                                    <label className="label">Title</label>
                                </div>
                                <div className="field-body">
                                    <div className="field">
                                        <div className="control">
                                            <input type="text" className="input" value={title} placeholder="Title" onChange={(e) => setTitle(e.target.value)} required></input>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="field is-horizontal">
                                <div className="field-label is-normal">
                                    <label className="label">Price</label>
                                </div>
                                <div className="field-body">
                                    <div className="field">
                                        <div className="control">
                                            <input type="text" name="password" className="input" value={price} placeholder="Price" onChange={(e) => setPrice(e.target.value)} required></input>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <hr></hr>
                            <div className="field is-horizontal">
                                <div className="field-label is-normal"></div>
                                <div className="field-body">
                                    <div className="field">
                                        <div className="control">
                                            <button type="submit" className="button is-primary">
                                                Submit
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
            </section>
        </div><div className="card container mt-5">
                <div className="m-5 p-5">
                    <form onSubmit={submitUser}>
                        <div className="field">
                            <label className="label">Name</label>
                            <input className="input" type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)}></input>
                        </div>
                        <div className="field">
                            <label className="label">Email</label>
                            <input className="input" type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)}></input>
                        </div>
                        <div className="field">
                            <label className="label">Password</label>
                            <input className="input" type="password" placeholder="password" value={password} onChange={(e) => setPassword(e.target.value)}></input>
                        </div>

                        <div className="field">
                            <button type="submit" className="button is-primary">Submit</button>
                        </div>
                    </form>
                </div>
            </div></>
    )
}

export default UserAdd;
