import axios from "axios";
import { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import jwt_decode from "jwt-decode";

const UserList = () => {

    const [users, setUsers] = useState([]);
    const [token, setToken] = useState([]);
    const [expire, setExpire] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        refreshToken();
        getUsers();
    }, []);

    const refreshToken = async () => {
        try {
            const response = await axios.get('http://localhost:3001/token');
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setExpire(decoded.exp);
        } catch (error) {
            if (error.response) {
                navigate('/')
            }
        }
    }

    const axiosJWT = axios.create()

    axiosJWT.interceptors.request.use( async(config) => { //untuk melakukan pengecekan sebelum req
        const currentDate = new Date()  // ambil waktu sekarang
        if(expire * 1000 < currentDate.getTime()){ // dikali 1000 karena dalam milisecond 
            const response = await axios.get('http://localhost:3001/token') // jika waktu expire lebih kecil maka panggil API token
            // update token untuk authorization yang di header
            config.headers.Authorization = `Bearer ${response.data.accessToken}` //ambil access token baru dari API 
            setToken(response.data.accessToken) // set kembali token baru untuk digunakan lagi untuk refreshToken
            const decoded = jwt_decode(response.data.accessToken) // decode token baru
            setExpire(decoded.exp) // perbarui data expire dengan decode token baru
        }
        return config
    }, (error) => {
        return Promise.reject(error) //
    })

    const getUsers = async () => {
        const response = await axiosJWT.get('http://localhost:3001/user', {
            headers: {
                Authorization: `Bearer ${token}`
            }
        });
        setUsers(response.data);
    }

    const deleteUser = async (id) => {
        await axiosJWT.delete('http://localhost:3001/user/delete/' + id, {
            headers: {
                Authorization: `Bearer ${token}`
            }
        });
        getUsers();
    }

    return (
        
        <div id='app'>
            <Navbar />
            <Sidebar />
            <section class="section is-title-bar">
                <div class="level">
                    <div class="level-left">
                        <div class="level-item">
                        <ul>
                            <li>Admin</li>
                            <li>Product</li>
                        </ul>
                        </div>
                    </div>
                    <div class="level-right">
                        <div class="level-item">
                        <div class="buttons is-right">
                            <Link to="/product/add" className="button is-primary">
                                <span class="icon"><i class="mdi mdi-product"></i></span>
                                <span>Add Product</span>
                            </Link>
                        </div>
                        </div>
                    </div>
                </div>
            </section>
            <section class="hero is-hero-bar">
                <div class="hero-body">
                    <div class="level">
                        <div class="level-left">
                        <div class="level-item"><h1 class="title">
                            Product List
                        </h1></div>
                        </div>
                        <div class="level-right">
                            <div class="level-item"></div>
                        </div>
                    </div>
                </div>
            </section>
            <section class="section is-main-section">
                <div class="card">
                    <header class="card-header">
                        <p class="card-header-title">
                            <span class="icon"><i class="mdi mdi-table default"></i></span>
                            Products
                        </p>
                    </header>
                    <div class="card-content">
                        <div class="field is-horizontal">
                            <table className="table is-striped is-fullwidth">
                                <thead>
                                    <tr>
                                        <th>No</th>
                                        <th>Title</th>
                                        <th>Price</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    { products.map((product, index) => (
                                    <tr key={ product.id }>
                                        <td>{ index + 1 }</td>
                                        <td>{ product.title }</td>
                                        <td>{ product.price }</td>
                                        <td className="is-actions-cell">
                                            <div className="buttons">
                                                <Link to={'/product/edit/' + product.id} className="button is-small is-primary" type="button">
                                                    <span className="icon"><i className="mdi mdi-eye"></i></span>
                                                </Link>
                                                <button onClick={() => deleteProduct(product.id)} className="button is-small is-danger jb-modal" data-target="sample-modal" type="button">
                                                    <span className="icon"><i className="mdi mdi-trash-can"></i></span>
                                                </button>
                                            </div>
                                        </td>
                                    </tr>
                                    )) }
                                </tbody>
                            </table>
                        </div>
                        <hr></hr>
                    </div>
                </div>
            </section>
        </div>

        // <div className='container mt-5'>
            
        //     <Link to="/user/add" className="button is-primary">Add User</Link>
        //     <table className="table is-striped is-fullwidth">
        //         <thead>
        //             <tr>
        //                 <th>No</th>
        //                 <th>Name</th>
        //                 <th>Email</th>
        //                 <th>Action</th>
        //             </tr>
        //         </thead>
        //         <tbody>
        //             { users.map((user, index) => (
        //             <tr key={ user.id }>
        //                 <td>{ index + 1 }</td>
        //                 <td>{ user.name }</td>
        //                 <td>{ user.email }</td>
        //                 <td className="is-actions-cell">
        //                     <div className="buttons">
        //                         <Link to={'/user/edit/' + user.id} className="button is-small is-primary" type="button">
        //                             <span className="icon"><i className="mdi mdi-eye"></i></span>
        //                         </Link>
        //                         <button onClick={() => deleteUser(user.id)} className="button is-small is-danger jb-modal" data-target="sample-modal" type="button">
        //                             <span className="icon"><i className="mdi mdi-trash-can"></i></span>
        //                         </button>
        //                     </div>
        //                 </td>
        //             </tr>
        //             )) }
        //         </tbody>
        //     </table>
        // </div>
    )
}

export default UserList;
