import axios from "axios";
import { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import jwt_decode from "jwt-decode";

const ProductsList = () => {

    const [products, setProduct] = useState([]);
    const [token, setToken] = useState([]);
    const [expire, setExpire] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        refreshToken();
        getProducts();
    });

    const refreshToken = async () => {
        try {
            const response = await axios.get('http://localhost:3001/token');
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setExpire(decoded.exp);
        } catch (error) {
            if (error.response) {
                navigate('/')
            }
        }
    }

    const axiosJWT = axios.create()

    axiosJWT.interceptors.request.use(async (config) => {
        const currentData = new Date();
        if (expire * 1000 < currentData.getTime()) {
            const response = await axios.get('http://localhost:3001/token');
            config.headers.Authorization = `Bearer ${response.data.accessToken}`;
            setToken(response.data.accessToken);
            const decoded = jwt_decode(response.data.accessToken);
            setExpire(decoded.exp);
        }
        return config;
    }, (error) => {
        return Promise.reject(error);
    });


    const getProducts = async () => {
        const response = await axiosJWT.get('http://localhost:3001/product', {
            headers: {
                Authorization: `Bearer ${token}`
            }
        });
        setProduct(response.data);
    }

    const deleteProduct = async (id) => {
        const response = await axiosJWT.delete('http://localhost:3001/product/delete/' + id, {
            headers: {
                Authorization: `Bearer ${token}`
            }
        });
        getProducts();
    }

    return (
        <div className='container mt-5'>
            
            <Link to="/product/add" className="button is-primary">Add Product</Link>
            <table className="table is-striped is-fullwidth">
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Title</th>
                        <th>Price</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    { products.map((product, index) => (
                    <tr key={ product.id }>
                        <td>{ index + 1 }</td>
                        <td>{ product.title }</td>
                        <td>{ product.price }</td>
                        <td className="is-actions-cell">
                            <div className="buttons">
                                <Link to={'/product/edit/' + product.id} className="button is-small is-primary" type="button">
                                    <span className="icon"><i className="mdi mdi-eye"></i></span>
                                </Link>
                                <button onClick={() => deleteProduct(product.id)} className="button is-small is-danger jb-modal" data-target="sample-modal" type="button">
                                    <span className="icon"><i className="mdi mdi-trash-can"></i></span>
                                </button>
                            </div>
                        </td>
                    </tr>
                    )) }
                </tbody>
            </table>
        </div>
    )
}

export default ProductsList;
